      const {
        userId
      } = newData[i];
      if (!(userId in dico)) {
        dico[userId] = 0;
      }
      if (newData[i].completed) {
        dico[userId] += 1;
      }
    }
    for (const key in dico) {
      if (Object.prototype.hasOwnProperty.call(dico, key)) {
        const value = dico[key];
        if (value === 0) {
          delete dico[key];
        }
      }
    }
    console.log(dico);
  }
});
